// Get the list of previously sent apartment links from flow context
let sentApartments = flow.get('sentApartments') || [];

// Initialize an array to store the formatted messages
let formattedMessages = [];

// Initialize an array to store the links of new apartments
let newApartments = [];

// Iterate over each apartment in the combined data
msg.payload.forEach(apartment => {
    let apartmentDetails = apartment.apartmentDetails;
    let link = apartment.link;

    // Check if the apartment has been sent before (based on its link)
    if (!sentApartments.includes(link)) {
        // If not sent before, mark it as new and add it to newApartments
        newApartments.push(link);

        // Extract the address (the part after the "|")
        let parts = apartmentDetails.split('|');
        let address = parts.length > 1 ? parts[1].trim() : "Unknown location"; // Get the address after the "|"

        // Create a Google Maps link based on the address
        let googleMapsLink = `https://www.google.com/maps?q=${encodeURIComponent(address)}`;

        // Format the message for Telegram
        let message = `Apartment Details: ${apartmentDetails}\n\n` +
            `More Info: [Click here](${link})\n` + // The apartment link
            `View on Google Maps: [Google Maps](${googleMapsLink})`; // Google Maps link

        // Add the formatted message to the array
        formattedMessages.push({
            chatId: "672048596",  // Replace with your actual Telegram chat ID
            type: "message",
            content: message,
            options: {
                parse_mode: "Markdown"
            }
        });
    }
});

// If there are any new apartments, proceed with sending them to Telegram
if (formattedMessages.length > 0) {
    // Update the list of sent apartments by adding the new apartments
    sentApartments = [...sentApartments, ...newApartments];
    flow.set('sentApartments', sentApartments);  // Store the updated list in flow context

    // Set the formatted messages as the payload to send to Telegram
    msg.payload = formattedMessages;
} else {
    msg.payload = "No new apartments to send.";
}

return msg;

